#define vi vector<int>
class Solution {
public:
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {

        auto compare1 = [](auto &p1, auto &p2) {
            if(p1[0] == p2[0])
                return p1[1] > p2[1];
            else
                return p1[0] > p2[0];
        };

        auto compare2 = [](auto &p1, auto &p2) {
            return p1[2] > p2[2];
        };

        // Sorted on the basis of server weight first and on the basis of index second
        // pq -> {server_weight, server_idx}

        priority_queue<vi, vector<vi>, decltype(compare1)> available(compare1);

        // sorted on the basis of endTimeOfCompletionOfTask

        // pq -> {server_weight, server_idx, endTimeOfCompletionOfTask}
        priority_queue<vi, vector<vi>, decltype(compare2)> notAvailable(compare2);

        for(int i=0; i<servers.size(); i++) {
            available.push({servers[i], i});
        }

        vector<int> res;
        int time = 0;

        for(int i=0; i<tasks.size(); i++) {

            // It might be possible that time value becomes greater than i in the case
            // when no server is available
            time = max(time, i);

            // If no server is available then we just fast forward the time to the 
            // completion time of the first non available server

            if(available.size() == 0) {
                time = notAvailable.top()[2];
            }

            // If at this current instance of time there are servers which need to be 
            // pushed again into the available if they are completed do it
            while(notAvailable.size() != 0 and notAvailable.top()[2] <= time) {
                auto t = notAvailable.top();
                notAvailable.pop();
                available.push({t[0], t[1]});
            }

            // Finally assign the task the server for processing
            if(available.size() != 0) {
                auto t = available.top();
                available.pop();
                res.push_back(t[1]);
                notAvailable.push({t[0], t[1], time + tasks[i]});
            }
        }

        return res;
    }
};
