class Solution {
public:
    struct ServerInfo {
        int id;
        int weight;
        long long availableTime;
        explicit ServerInfo(int id, int weight, int availableTime) 
                : id(id), weight(weight), availableTime(availableTime) {}
    };
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {
        // At second j, the jth task is inserted into the queue
        // task at front will be assigned to a free server with the smallest (weight, index)

        // setup:
        // - use two priority queues (min heaps) to hold servers, 
        //   - one for working servers, sorted by (availableTime)
        //   - another one for free servers, sorted by (weight, index)
        // - use a queue to hold available tasks
        // - use a variable curTime and increase it at each round
        auto customGreaterAvailableTime = [](const auto& lhs, const auto& rhs) -> bool {
            return lhs.availableTime > rhs.availableTime;
        };
        auto customGreaterWeight = [](const auto& lhs, const auto& rhs) -> bool {
            if (lhs.weight == rhs.weight) {
                return lhs.id > rhs.id;
            }
            return lhs.weight > rhs.weight;
        };
        priority_queue<ServerInfo, vector<ServerInfo>, decltype(customGreaterAvailableTime)> 
                workingServerInfoPQ(customGreaterAvailableTime);
        priority_queue<ServerInfo, vector<ServerInfo>, decltype(customGreaterWeight)> 
                freeServerInfoPQ(customGreaterWeight);
        long long curTime = 0;
        for (int i = 0; i < servers.size(); ++i) {
            freeServerInfoPQ.emplace(i, servers[i], curTime);
        }
        queue<int> taskProcessingTimeQ;

        // at each round, 
        // - check workingServerPQ and move free ones to freeServerPQ
        // - push one task into the queue
        // - try assign the task at front of the queue to a free server
        // - increase current timestamp
        vector<int> result;
        result.reserve(tasks.size());
        while (curTime < tasks.size() || !taskProcessingTimeQ.empty()) {
            while (!workingServerInfoPQ.empty() && workingServerInfoPQ.top().availableTime == curTime) {
                auto top = workingServerInfoPQ.top();
                top.availableTime = curTime;
                freeServerInfoPQ.emplace(std::move(top));
                workingServerInfoPQ.pop();
