struct Server {
    int weight;
    int idx;

    bool operator<(const Server& a) const {
        if (weight == a.weight) return idx < a.idx;
        return weight < a.weight;
    }
};

class ServerRack {
public:
    explicit ServerRack(const vector<int>& servers) {
        if (servers.empty()) throw logic_error("Bad Test Case");

        for(int i = 0; i < servers.size(); ++i) {
            m_availableServers.emplace(Server{servers[i], i});
        }
    }

    pair<int, int> getAvailableServer(int time, int taskTime) {
        time = elapseUntilFree(time);

        Server server = m_availableServers.top();
        m_availableServers.pop();

        m_busyServers.emplace(time + taskTime, server);
        return {time, server.idx};
    }


private:
    struct AvailableServerCompare {
        bool operator()(const Server& a, const Server& b) {
            return a.weight == b.weight
                ? a.idx > b.idx
                : a.weight > b.weight;
        }
    };

    priority_queue<Server, vector<Server>, AvailableServerCompare> m_availableServers;
    priority_queue<pair<int, Server>, vector<pair<int, Server>>, greater<>> m_busyServers;

    int elapseUntilFree(int currentTime) {
        while(!m_busyServers.empty() && m_busyServers.top().first <= currentTime) {
            m_availableServers.emplace(m_busyServers.top().second);
            m_busyServers.pop();
        }

        if (m_availableServers.empty()) {
            return elapseUntilFree(m_busyServers.top().first);
        }

        return currentTime;
    }
};

class Solution {
public:
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {
        vector<int> results;
        results.reserve(tasks.size());

        int time = 0;
        ServerRack rack(servers);
        for(int i = 0; i < tasks.size(); ++i) {
            int taskTime = tasks[i];

            auto [availableTime, serverIdx] = rack.getAvailableServer(time, taskTime);
            results.push_back(serverIdx);

            if (availableTime <= i) {
                // Increment time when we are having no trouble processing the queue
                time = i + 1;
            } else {
                // Don't incremement  if we're behind on the queue
                time = availableTime;
            }
        }

        return results;
    }
};
