class Server {
    int index;
    int weight;
    int time;

    public Server(int index, int weight){
        this.index = index;
        this.weight = weight;
    }

     public Server(int index, int weight, int time){
        this.index = index;
        this.weight = weight;
        this.time = time;
    }
}

class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {

        PriorityQueue<Server> available = new PriorityQueue<>((a,b) -> a.weight == b.weight ? Integer.compare(a.index,b.index):Integer.compare(a.weight, b.weight));
         PriorityQueue<Server> unavailable = new PriorityQueue<>((a,b) -> a.time - b.time);

        for(int i = 0; i < servers.length; i++){
            available.add(new Server(i, servers[i]));
        }

        int time = 0;
        int j = 0;
        int[] resultant = new int[tasks.length];

        for(int i = 0; i < tasks.length; i++){
            time = Math.max(time, i);

            if(available.isEmpty()){
                time = unavailable.peek().time;
            }

            while(!unavailable.isEmpty() && unavailable.peek().time <= time){
                Server availableServer = unavailable.poll();
                available.add(new Server(availableServer.index, availableServer.weight));
            }

            if(!available.isEmpty()){
                Server server = available.poll();
                unavailable.add(new Server(server.index, server.weight, time + tasks[i]));
                resultant[j++] = server.index;
            }
        }

        return resultant;
    }
}
