class Solution {
    class Server{
        int weight;
        int index;
        int finishTime;
        Server(int w,int i,int ft){
            this.weight=w;
            this.index=i;
            this.finishTime=ft;
        }
    }
    public int[] assignTasks(int[] servers, int[] tasks) {
        Comparator<Server> cmp1=new Comparator<>(){
            public int compare(Server s1,Server s2){
                if(s1.weight==s2.weight){
                    return s1.index-s2.index;
                }else{
                    return s1.weight-s2.weight;
                }
            }
        };
        Comparator<Server> cmp2=new Comparator<>(){
            public int compare(Server s1,Server s2){
                if(s1.finishTime==s2.finishTime){
                    if(s1.weight==s2.weight){
                        return s1.index-s2.index;
                    }else{
                        return s1.weight-s2.weight;
                    }    
                }else{
                     return s1.finishTime-s2.finishTime;
                }
            }
        };
        PriorityQueue<Server> freeServers=new PriorityQueue<>(cmp1);
        for(int i=0;i<servers.length;i++){
            Server s=new Server(servers[i],i,Integer.MIN_VALUE);
            freeServers.offer(s);
        }
        PriorityQueue<Server> busyServers=new PriorityQueue<>(cmp2);
        int[] ans=new int[tasks.length];
        for(int i=0;i<tasks.length;i++){
            //move servers that are now idle from busy servers to free servers
            int curTime=i;
            while(!busyServers.isEmpty() && busyServers.peek().finishTime==curTime){
                freeServers.offer(busyServers.poll());
            }
            if(freeServers.isEmpty()){
                Server s=busyServers.poll();
                s.finishTime=s.finishTime+tasks[i];
                ans[i]=s.index;
                busyServers.offer(s);
            }else{
                Server s=freeServers.poll();
                ans[i]=s.index;
                s.finishTime=curTime+tasks[i];
                busyServers.offer(s);  
            }

        }
        return ans;
    }
}
