class Solution {
public:
    class comp {
    public:
        bool operator()(pair<int, pair<int,int>>& a, pair<int, pair<int,int>>& b) {
            if(a.first!=b.first) return a.first>b.first;
            if(a.second.first!=b.second.first) return a.second.first>b.second.first;
            return a.second.second>b.second.second;
        }
    };
    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {
        int n = servers.size(), m = tasks.size();
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;
        priority_queue<pair<int, pair<int, int>>, vector<pair<int, pair<int, int>>>, comp> busy;
        for(int i=0; i<n; i++) {
            pq.push({servers[i], i});
        }
        vector<int> ans(m);
        int i = 0, tim = 0;
        while(i<m) {
            tim = max(tim, i);
            if(pq.empty() && !busy.empty() && busy.top().first>tim) {
                tim = busy.top().first;
            }
            while(!busy.empty() && busy.top().first<=tim) {
                pq.push(busy.top().second);
                busy.pop();
            }
            while(i<=tim && i<m && !pq.empty()) {
                ans[i] = pq.top().second;
                busy.push({tim + tasks[i], pq.top()});
                pq.pop();
                ++i;
            }
        }
        return ans;
    }
};
