func assignTasks(servers []int, tasks []int) []int {
    n := len(tasks)
    order := []int{}
    busy := TaskHeap{}
    free := ServerHeap{}

    for i, server := range servers {
        heap.Push(&free, Server{index: i, weight: server})
    }

    t := 0
    i := 0

    for len(order) < n {
        for busy.Len() > 0 && busy[0].end <= t {
            task := heap.Pop(&busy).(Task)
            heap.Push(&free, Server(task.server))
        }

        if free.Len() == 0 {
            // fmt.Printf("t = %d, skipped to %d \n", t, busy[0].end)
            t = busy[0].end
            continue
        }

        // fmt.Printf("t = %d, free = %+v, busy = %+v \n", t, free, busy)

        task := tasks[i]
        server := heap.Pop(&free).(Server)
        order = append(order, server.index)
        heap.Push(&busy, Task{index: i, second: task, end: t + task, server: server})
        i += 1

        if i > t {
            t += 1
        }
    }

    return order
}

type Task struct {
	index      int
    second     int
    end        int
    server     Server
}

type TaskHeap []Task

func (h TaskHeap) Len() int { return len(h) }
func (h TaskHeap) Less(i, j int) bool {
	return h[i].end < h[j].end
}
func (h TaskHeap) Swap(i, j int) { h[i], h[j] = h[j], h[i] }

func (h *TaskHeap) Push(x interface{}) {
	*h = append(*h, x.(Task))
}

func (h *TaskHeap) Pop() interface{} {
    x := (*h)[len(*h)-1]
    *h = (*h)[0:len(*h)-1]
	return x
}

type Server struct {
	index      int
    weight     int
}

type ServerHeap []Server

func (h ServerHeap) Len() int { return len(h) }
func (h ServerHeap) Less(i, j int) bool {
	if h[i].weight == h[j].weight {
		return h[i].index < h[j].index
	}

	return h[i].weight < h[j].weight
}
func (h ServerHeap) Swap(i, j int) { h[i], h[j] = h[j], h[i] }

func (h *ServerHeap) Push(x interface{}) {
	*h = append(*h, x.(Server))
}

func (h *ServerHeap) Pop() interface{} {
    x := (*h)[len(*h)-1]
    *h = (*h)[0:len(*h)-1]
	return x
}
