class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {
        //0 is index, 1 is weight, 2 is available time
        PriorityQueue<int[]> busyServer = new PriorityQueue<int[]>((a, b) -> a[2] - b[2]);
        //0 is index, 1 is weight, 2 is available time
        PriorityQueue<int[]> freeServer = new PriorityQueue<int[]>((a, b) -> a[1] == b[1] ? a[0] - b[0] : a[1] - b[1]);
        for(int i = 0; i < servers.length; i++){
            freeServer.offer(new int[]{i, servers[i], 0});    
        }
        int time = 0;
        int[] ans = new int[tasks.length];
        for(int j = 0; j < tasks.length; j++){
            time = Math.max(time, j);
            while(!busyServer.isEmpty() && busyServer.peek()[2] <= time){
                int[] free = busyServer.poll();
                freeServer.offer(free);
            }
            if(freeServer.isEmpty()){
                time = busyServer.peek()[2];
                while(!busyServer.isEmpty() && busyServer.peek()[2] <= time){
                    int[] free = busyServer.poll();
                    freeServer.offer(free);
                }                
            }
            int[] assigned = freeServer.poll();
            assigned[2] = time + tasks[j];
            busyServer.offer(assigned);
            ans[j] = assigned[0];
        }
        return ans;
    }
}
//How to analyze time complexity?
//First, we have to notice that whenever there are free servers, each round in for-loop will only take O(log#server)
//Second, if there is no free server, we may put x busy servers into free server queue; after this, the next "x" rounds
//will only take O(log#server) each round again.
//Therefore, time complexisty is only O(#task*log(#server)), which is O(nlogn)
