```csharp
public class Solution {
    public int[] AssignTasks(int[] servers, int[] tasks) {
        // start with servers with minimum weight
        int[] result = new int[tasks.Length];
        var freeServers = new PriorityQueue<(int weight, int index), (int weight, int index)>();
        // time - time when server becomes free
        var usedServers = new PriorityQueue<(int time, int weight, int index), (int time, int weight, int index)>();

        for (int i = 0; i < servers.Length; i++) {
            freeServers.Enqueue((servers[i], i), (servers[i], i));
        }
        int time = 0;
        for (int i = 0; i < tasks.Length; i++) {
            // by default, time == i, but it can be overriden (advanced)
            time = Math.Max(time, i); // if time was overriden (shifted), because there were no available servers, take that 'time'

            // there are no servers available - advance time to the next available time
            if (freeServers.Count == 0) {
                time = usedServers.Peek().time; // override time to the next available period. This is the time when a first server will be available
            }
            // check if any server became available - first try to pop used servers if available and put them into the free queue
            while(usedServers.Count > 0 && time >= usedServers.Peek().time) { // first available server for that time
                var (timeFree, w, idx) = usedServers.Dequeue(); // timeFree - used for priority
                freeServers.Enqueue((w, idx), (w, idx));
            }
            var (weight, index) = freeServers.Dequeue();
            result[i] = index;
            int timeServerBecomesFree = time + tasks[i];
            usedServers.Enqueue((timeServerBecomesFree, weight, index), (time + tasks[i], weight, index));
        }
        return result;
    }
}
