        vector<int>tmp(n,0);
         int tme=0;
        for(int i=0;i<m;i++)
        {  
            while(pq1.size()>0&&pq1.top().first<=i)
            {
                pair<int,int>p1=pq1.top();
                pq1.pop();
                pq.push({servers[p1.second],p1.second});
