class Solution {
public:

    class ComparisonClass {
        public:
            bool operator() (pair<int, int> &a, pair<int, int> &b) {
                return a.first == b.first ? a.second > b.second : a.first > b.first;
            }
    };

    vector<int> assignTasks(vector<int>& servers, vector<int>& tasks) {

        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> busy;
        priority_queue<pair<int, int>, vector<pair<int, int>>, ComparisonClass> free;

        for(int i=0;i<servers.size();i++){
            free.push({servers[i], i});
        }

        vector<int> res;

        int i = 0;
        int currTime = 0;

        while(i<tasks.size()){

            while(!busy.empty() && busy.top().first <= i){
                auto top = busy.top();
                busy.pop();
                free.push({servers[top.second], top.second});
            }

            if(free.empty()){
                auto top = busy.top();
                currTime = top.first;
                busy.pop();
                free.push({servers[top.second], top.second});
                while(!busy.empty() && busy.top().first == top.first){
                    top = busy.top();
                    busy.pop();
                    free.push({servers[top.second], top.second});
                }
            }
            else if(currTime < i) currTime = i;

            auto top = free.top();
            free.pop();
            res.push_back(top.second);
            int expire = tasks[i]+currTime;
            busy.push({expire, top.second});
            i++;

        }

        return res;
    }
};
