class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {
        PriorityQueue<int[]> serverQ = new PriorityQueue<>(
            (int[] s1, int[] s2) -> (s1[1] != s2[1]) ? Integer.compare(s1[1], s2[1]) : Integer.compare(s1[0], s2[0])
        );

        PriorityQueue <int[]> jobsQ = new PriorityQueue<>( (int[] s1, int[] s2) -> Integer.compare(s1[2], s2[2]));

        for (int i = 0; i < servers.length; i++) {
            // 0 => server index
            // 1 => priority
            // 2 => time available
            serverQ.add(new int[]{i, servers[i], 0});
        }

        int[] ans = new int[tasks.length];

        for (int t = 0; t < tasks.length; t++) {
			// relese servers which done by time t
            while ( ! jobsQ.isEmpty() && jobsQ.peek()[2] <= t) {
                serverQ.add(jobsQ.poll());
            }

			// if still no server available by time t, release all servers which availalbe at next t
            if (serverQ.isEmpty()) {
                int ti = jobsQ.peek()[2];
                while (! jobsQ.isEmpty() && jobsQ.peek()[2] == ti) {
                    serverQ.add(jobsQ.poll());    
                }
            }

            int[] s = serverQ.poll();

            ans[t] = s[0];

            s[2] = (s[2] < t) ? (t+tasks[t]) : (s[2]+tasks[t]);
            jobsQ.add(s);
        }

        return ans;
    }
}
