    public int[] assignTasks(int[] servers, int[] tasks) {
        PriorityQueue<Server> occupied = new PriorityQueue<>((a, b) -> {
            if (a.endTime != b.endTime) {
                return a.endTime - b.endTime;
            } else if (a.weight != b.weight){
                return a.weight - b.weight;
            } else {
                return a.index - b.index;
            }
        });

        PriorityQueue<Server> available = new PriorityQueue<>((a, b) -> {
            if (a.weight != b.weight){
                return a.weight - b.weight;
            } else {
                return a.index - b.index;
            }
        });
        for (int i = 0; i < servers.length; i++) {
            available.add(new Server(i, 0, servers[i]));
        }
        int[] res = new int[tasks.length]; 
        for (int j = 0; j < tasks.length; j++) {
		//when a server is available, put it into the "available" heap 
            while (!occupied.isEmpty() && occupied.peek().endTime <= j) {
                Server cur = occupied.poll();
                available.add(cur);
            }
		//always check wether there is an available server  
            if (!available.isEmpty()) {
                Server toUse = available.poll();
                toUse.endTime = j + tasks[j];
                res[j] = toUse.index;
                occupied.add(toUse);                
            } else {    //if there is no available servers, use the server that has earliset end time, smallest weight and index  
                Server toUse = occupied.poll();
                toUse.endTime += tasks[j];
                res[j] = toUse.index;
                occupied.add(toUse);         
            }
        }
        return res;
    }
    class Server{
        int endTime;
        int index;
        int weight;
        public Server(int index, int t, int w) {
            this.index = index;
            this.endTime = t;
            this.weight = w;
        }
    }
}
