class Solution {
    class Server{
        int time;int weight;int idx;
        Server(int t, int w, int i) {
            time = t;weight = w;idx = i;
        }
    }
    class WeightComparator implements Comparator<Server> {
        public int compare(Server one, Server two) {
            int c = Integer.compare(one.weight, two.weight);
            if (c == 0) c = Integer.compare(one.idx, two.idx);
            return c;
        }
    }
    class TimeComparator implements Comparator<Server> {
        public int compare(Server one, Server two) {
            int c = Integer.compare(one.time, two.time);
            return c;
        }
    }
    public int[] assignTasks(int[] servers, int[] tasks) {
        PriorityQueue<Server> available = new PriorityQueue(new WeightComparator());
        PriorityQueue<Server> occupied = new PriorityQueue(new TimeComparator());

        for (int i = 0; i < servers.length; i++) {
            available.offer (new Server(0, servers[i], i));
        }

        int t = 0;
        for (int i = 0; i < tasks.length; i++) {
            t = Math.max(i, t);
            if (available.isEmpty()) {
                t = occupied.peek().time;
            }
            while (!occupied.isEmpty() && occupied.peek().time <= t) {
                available.offer(occupied.poll());
            }
            Server s = available.poll();
            s.time = t + tasks[i];
            tasks[i] = s.idx;
            occupied.offer(s);
        }
        return tasks;
    }
}
