class ServerPair {
  int weight;
  int index;

  ServerPair(int weight, int index){
    this.weight = weight;
    this.index = index;
  }
 }

 class Triplet {
  int timeFree;
  int weight;
  int index;

  Triplet(int timeFree, int weight, int index){
    this.timeFree = timeFree;
    this.weight = weight;
    this.index = index;
  }
 }

class Solution {
    public int[] assignTasks(int[] servers, int[] tasks) {
       PriorityQueue<ServerPair> available = new PriorityQueue<>(( ServerPair i1, ServerPair i2)->
         {
             if(i1.weight==i2.weight)
             {
                 return i1.index - i2.index;
             }
             else
             {
                 return i1.weight - i2.weight;
             }
         });
        for(int i = 0; i < servers.length; i++)
        {
            available.offer(new ServerPair(servers[i],i));
        }

        PriorityQueue<Triplet> unavail = new PriorityQueue<>(( Triplet i1, Triplet i2)->
         {
             if(i1.timeFree==i2.timeFree)
             {
                 return i1.index - i2.index;
             }
             else
             {
                 return i1.timeFree - i2.timeFree;
             }
         });
        int time = 0;
        int[] res = new int[tasks.length];
        for(int i = 0; i < tasks.length; i++)
        {
            time = Math.max(time, i);
            if(available.size()==0){
                time = unavail.peek().timeFree;
            }
            while(unavail.size() > 0 && time >= unavail.peek().timeFree){
              Triplet t = unavail.poll();
              available.offer(new ServerPair(t.weight, t.index));
            }

            ServerPair server = available.poll();
            res[i] = server.index;
            unavail.offer(new Triplet(time+tasks[i], server.weight, server.index));
        }

        return res;
    }
}

