class Solution {
public:
    string removeDuplicates(string s) {
        int n = s.length();
        int i=0;
        while(i<=n-1)
        {
            if(s[i]==s[i+1])
            {
                i--;
                s.erase(s.begin()+i+2);
                s.erase(s.begin()+i+1);
                n=n-2;
            }
            else
            {
                i++;
            }
            if(i<1) i=0;
        }
        return s;
    }
};
