class Solution {
    public String removeDuplicates(String S) {
        Deque<Character> deque = new LinkedList();

        for(char c : S.toCharArray()){
            //if last character same as current, 
            //then just pop last one
            if(!deque.isEmpty() && deque.peek() == c){
                deque.pop();
            }else{
                deque.push(c);
            }
        }

        //get all the left character
        StringBuilder sb = new StringBuilder();
        while(!deque.isEmpty()){
            sb.append(deque.pop());
        }
        //reverse the string
        sb.reverse();

        return sb.toString();
    }
}
