class Solution {
public:
    using VI = vector<int>;
    bool threeConsecutiveOdds(VI& A) {
        return accumulate(A.begin(), A.end(), false, [i = -1, &A](auto found, auto _) mutable {
            return ++i < 2 ? found : found || A[i - 2] & A[i - 1] & A[i] & 1;
        });
    }
};
```

