class Solution {
public:
	bool threeConsecutiveOdds(vector<int>& arr) {

	int j = 0, i = 0;
	deque<int> dq;

	while(j < arr.size()) {

		if(arr[j] % 2 != 0) {
			dq.push_back(arr[j]);
		}

		if(j - i + 1 == 3) {
			if(dq.size() == 3) return true;

			if(!dq.empty() and arr[i] == dq.front()) dq.pop_front();

			i++;
		}
		j++;
	}
	return false;
	}
};

