class Solution:
    def threeConsecutiveOdds(self, arr: List[int]) -> bool:
        if len(arr) < 3:
            return False

        a, b, c = arr.pop(0), arr.pop(0), arr.pop(0)
        if all(x % 2 == 1 for x in (a, b, c)):
            return True

        while arr:
            a, b, c = b, c, arr.pop(0)
            if all(x % 2 == 1 for x in (a, b, c)):
