    def threeConsecutiveOdds(self, arr: List[int]) -> bool:

        size = len(arr)

        is_odd = lambda x: (x % 2 == 1)

        if size < 3:
            # Quick rejection for small array which is impossible to have three consecutive odds
            return False

        else:

            for i in range( 1, size-1):

                if all( map(is_odd, arr[i-1:i+2] ) ):
					# check if we have three consecutive odds
