    while (m * (m + 1) / 2 < n)
        ++m;
    return m;    
}  
```
```
dp[m] = 1 + dp[m - 1] + m - 1;
```
```
dp[m][k] = 1 + dp[m - 1][k] + dp[m - 1][k - 1];
```
```cpp
int dp[1001][3] = {};
class Solution {
public:
int twoEggDrop(int n, int k = 2) {
    int m = 0;
    while (dp[m][k] < n) {
        ++m;
        for (int j = 1; j <= k; ++j)
            dp[m][j] = dp[m - 1][j - 1] + dp[m - 1][j] + 1;
    }
    return m;  
}
};
```
```cpp
int twoEggDrop(int n, int k = 2) {
    int dp[3] = {};
    int m = 0;
    while (dp[k] < n) {
        ++m;
        for (int j = k; j > 0; --j)
            dp[j] += dp[j - 1] + 1;
    }
    return m;
}
```

