        def dp(m, eggs):
            if eggs == 1 or m <= 1: return m # eggs = 1, you need to try the floors one by one
            res = float('inf')       
            for f in range(1, m + 1): # we do not know at which floor the egg will break
                res = min(res, 1 + max(dp(f - 1, eggs - 1), dp(m - f, eggs))) # min-max
            return res 

