```
class Solution {
public:
    using VI = vector<int>;
    using VVI = vector<VI>;
    int twoEggDrop(int N, int K = 2, int INF = 1e9 + 7) {
        VI pre(N + 1); iota(pre.begin(), pre.end(), 0);
        for (auto k{ 2 }; k <= K; ++k) {
            VI cur(N + 1, INF);
            for (auto n{ 1 }; n <= N; ++n)
                for (auto i{ 1 }; i <= n; ++i)
                    cur[n] = min(cur[n], 1 + (cur[i - 1] < INF ? max(pre[n - i], cur[i - 1]) : pre[n - i]));
            swap(pre, cur);
        }
        return pre[N];
    }
};
```

