class Solution:
    def twoEggDrop(self, n: int) -> int:

        @cache
        def fn(n, k): 
            """Return min moves for n floors and k eggs."""
            if k == 1: return n 
            if n == 0: return 0 
            ans = inf 
            for x in range(1, n+1): 
                ans = min(ans, 1 + max(fn(x-1, k-1), fn(n-x, k)))
            return ans 

        return fn(n, 2)
