class Solution {
    public int twoEggDrop(int n) {
        int eggs=2;
        int[][] dp=new int[n+1][3];
        for(int[] row : dp)
            Arrays.fill(row,-1);

        return solve(n,2,dp);
    }
      public int solve(int f,int e,int[][] dp)
    {
    if(f<=2 || e==1) return f;
        if(dp[f][e]!=-1) return dp[f][e];

        int min=f;

        for(int i=1;i<=f;++i)
        {
            int Break=solve(i-1,e-1,dp);
            int notBreak=solve(f-i,e,dp);
          //  System.out.print("1."+Break+"2."+notBreak+"-->");
            int worstCase=1+Math.max(Break,notBreak);

            min=Math.min(min,worstCase);
         //   System.out.print (worstCase+"--"+min+"==");
        }
       // System.out.println(f+"f"+e+"e"+min+"min");
        return dp[f][e]=min;
     }
}
```

