class Solution {
public:
    int twoEggDrop(int n) {
        int dp[n+1][2];
        dp[0][0]=0;dp[0][1]=0;
        dp[1][0]=1;dp[1][1]=1;
        for(int i=2;i<=n;i++){dp[i][0]=i;}
        for(int i=2;i<=n;i++)
        {
            dp[i][1]=INT_MAX;
            for(int x=1;x<=i;x++)
            {
                dp[i][1]=min({dp[i][1],1+max(dp[x-1][0],dp[i-x][1])});
            }
        }
        return dp[n][1];
    }
};
```
