var twoEggDrop = function(n) {
  const eggs = 2;
  const memo = new Array(n+1).fill(0).map(() => new Array(eggs + 1).fill(-1));

  function solve(n, eggs) {
    if (memo[n][eggs] !== -1) return memo[n][eggs];
    if (n <= 1 || eggs === 1) return n;  
    let min = Infinity;
    for (let i = 1; i <= n; i++) {
      // if egg breaks on ith floor solve for (i - 1, eggs - 1) and if eggs doesn't break on the ith floor
      // solve for (n - i, eggs)

      // Why choose max - because we need to consider the worst case
      const cur = Math.max(solve(i - 1, eggs - 1), solve(n - i, eggs)) + 1;
      min = Math.min(cur, min);
    }

    memo[n][eggs] =  min
    return memo[n][eggs];
  }

  return solve(n, eggs);
};
```

