class Solution {
public:
    std::unordered_map<int, int> memo;
    int drop(int K, int N) {
		// No floors left
        if (N == 0) return 0;
		// Only one egg left, test all floors
        if (K == 1) return N;

        const int key = (N << 2) + K;
        if (memo.count(key)) return memo[key];

        int res = std::numeric_limits<int>::max();
        int lo = 1, hi = N;
        while (lo <= hi) {
            int n = lo + (hi - lo) / 2;

			// count0 increases with n, count1 decreases
            int count0 = drop(K, n - 1);
            int count1 = drop(K - 1, N - n);
            res = std::min(res, 1 + std::max(count0, count1));
            (count0 > count1) ? hi = n - 1 : lo = n + 1;
        }

        return memo[key] = res;
    }

    int twoEggDrop(int n) {
        return drop(2, n);
    }
};
```

