class Solution {
public:
    int dp[1001];

    int f(int n){
        if(n==1)return 1;

        if(dp[n]!=-1)return dp[n];

        int l=1,r=n,ans=1e7,temp=0;
        while(l<=r){
            int mid=(l+r)/2;
            int down = mid-1;  // egg broke
            int up = f(n-mid); // egg didnt break;
            temp=1+max(up,down);
            if(down>up){
                r=mid-1;
            }
            else l=mid+1;

            ans=min(ans,temp);
        }
        return dp[n] = ans;
    }

    int twoEggDrop(int n) {
        memset(dp,-1,sizeof(dp));
        return f(n);
    }
};
