class Solution {
    public int twoEggDrop(int n) {
        int n1=n,c=0;
        if(n==1) return 1;
        if(n==2) return 2;
        for(int i=1;i<n1;i++){
            if(n>0){
                n=n-i;
                c=c+1;
            }
            else{
                break;
            }
        }
        return c;
    }
}
