class Solution {
public:
    vector<int> grayCode(int n) {
        vector<int> codes;

        for(string s : get_n_bit_gray_code(n))
            codes.push_back(stoi(s , 0 , 2)); // converting string-binary-num to int : "100" = 4
        return codes;
    }

    vector<string> get_n_bit_gray_code(int n)
    {
        if(n == 1) // base case
            return {"0" , "1"};

        vector<string> v = get_n_bit_gray_code(n - 1);

        vector<string> codes;

        for(int i = 0 ; i < v.size() ; ++i) // forward-fashion traversal to add 0 to front
            codes.push_back("0" + v[i]);

        for(int i = v.size() - 1 ; i >= 0 ; --i) // reverse-fashion traversal to add 1 to front
            codes.push_back("1" + v[i]);

        return codes;
    }
};
```

