class Solution {
    public List<Integer> grayCode(int n) {
        List<Integer> list = new ArrayList<>();

		// Handling negative n
        if (n < 0) {
            return list;
        }
		// Initial value
        list.add(0);

        int mirror = 0;
        while (mirror < n) {
            int count = list.size();
            for (int i = count-1; i >= 0; i--) {
                list.add((1 << mirror) | list.get(i)); // this will get the value from list and add 2^mirror to the number as we discussed above
            }
            mirror++;
        }
        return list;
    }
}
