    public class Solution {
        public List<Integer> grayCode(int n) {
            List<Integer> list = new LinkedList<Integer>();
            Stack<Integer> stack = new Stack<Integer>();
            list.add(0);
            int i = 0;
            while(i < n) {
                int len = list.size();
                int inc = (int)(Math.pow(2, i));
                for(int j=0; j<len; j++) {
                    stack.push(list.get(j));
                }
                while(!stack.isEmpty()) {
                    list.add(stack.pop() + inc);
                }
                i++;
            }
            return list;
        }
    }

