Well, I write a method using standard backtracking way. 

    class Solution {
    public:
        int Bit2Int(bitset<32> &c, int n)
        {
            int sum = 0;
            for(int i = 0; i < n; i++)
            {
                sum <<= 1;
                sum += c[i];
            }
            return sum;
        }

        void backtrack(bitset<32> &c, int k, int n, vector<int> &result)
        {
            if(++k == n)
                result.push_back(Bit2Int(c, n));
            else
            {
                backtrack(c, k, n, result);
                c.flip(k);
                backtrack(c, k, n, result);
            }
        }

        vector<int> grayCode(int n) {
            if(!n)
                return vector<int>{0};
            else if(n == 1)
                return vector<int>{0, 1};
            vector<int> result;
            bitset<32> c;
            backtrack(c, -1, n, result);
            return result;
        }
    };

