public List<Integer> grayCode(int n) {
        List<Integer> res = new ArrayList<>(1<<n);
        res.add(0);
        if (n == 0) {
            return res;
        }
        for (int i = 1; i <= n; i++) {
            int lastIdx = res.size() - 1;
            int add = 1 << (i - 1);
            for (int j = lastIdx; j >= 0; j--) {
                res.add(add + res.get(j));
            }
        }
        return res;
    }
