public List<Integer> grayCode(int n) {
        List<Integer> arr=new ArrayList<>();

        if(n==0)
        {
            arr.add(0);
            return arr;
        }
        arr.add(0);
        arr.add(1);
        int ptr=1;

        while(ptr<n){
            for(int i=arr.size()-1;i>=0;i--){
                arr.add((1<<ptr)+arr.get(i));
            }
            ptr++;
        }
        // initially 
        // 0
        // 1
        // arr = [0, 1]
        // now - for ptr=1, following is added
        // 11
        // 10
        // arr = [0, 1, 3, 2]
        // now for ptr=2, following is added
        // 101
        // 111
        // 101
        // 100
        // arr = [0, 1, 3, 2, 6, 7, 5, 4]
        // for each new ptr we add 1<<ptr to the reverse order of previous ptr values.
        // eg  6=(1<<2)+2
        // eg  7=(1<<2)+3
        // eg  5=(1<<2)+1
        // eg  4=(1<<2)+0
        // and [2, 3, 1, 0] are reverse of previous ptr arr values

        return arr;
