class Solution {
public:
    vector<int> grayCode(int n) {
        if(n==1) return {0,1};

        vector<int> p= grayCode(n-1);
        vector<int> q=p;reverse(q.begin(),q.end());
        int inc= 1<<(n-1);
        for(auto &i : q) i+=inc;
        p.insert(p.end(),q.begin(),q.end());

        return p;
    }
};

