class Solution {
    public List<Integer> grayCode(int n) {
        List<Integer> list = new ArrayList<>();
        list.add(0);
        int prev=0;
        int len=(int)Math.pow(2,n);


        for(int i=1;i<len/2;i++){
            int bitCount=getBitCount(i);

            if(Math.abs(bitCount-prev)!=1){
                list.add(i+1);
                list.add(i);
                i++;
            }
            else{
                list.add(i);
            }
             prev=bitCount;
        }

        for(int i=len-1;i>=len/2;i--){
            int bitCount=getBitCount(i);

            System.out.println(Math.abs(bitCount-prev)+" "+i+" "+bitCount+" "+prev);

            if(Math.abs(bitCount-prev)!=1){
                list.add(i-1);
                list.add(i);
                i--;
            }
            else{
                list.add(i);
            }
             prev=bitCount;
        }


        return list;
    }
    public int getBitCount(int n){
        int count=0;
        while(n>0){
            if((n&1)!=0)count++;
            n=n>>1;
        }
        return count;
    }
}
