class Solution {
public:
    void rotate(int nums[], int n, int k) {
        int nowIndex = 0, nextIndex;
		int tmp1, tmp2 = nums[0];
		for(int j=0,i=0; j<n; j++){
			tmp1 = tmp2;
			nowIndex = (k + nowIndex) % (n);
			tmp2 = nums[nowIndex];
			nums[nowIndex] = tmp1;
			if(nowIndex == i) {
				nowIndex = ++i;
