/*
* o(n)
* This solution is simply reversing the array and the reversing array from 0 to k-1 and then from k to n-1. 
* Do a dry run by taking an example on copy and you will usndersand it. 
*/
class Solution {
public:
    void rotate(vector<int>& nums, int k) {
        reverse(nums.begin(),nums.end());
        reverse(nums.begin(),nums.begin()+k%nums.size());
        reverse(nums.begin()+k%nums.size(),nums.begin()+nums.size());
    }
};

// o(n) Same as above but without using inbuilt function.
class Solution {
public:
    void rotate(vector<int>& nums, int k) {
        k%=nums.size();
        reverse(nums.begin(),nums.end());
        int i=0,j=k-1;
        while(i<j) swap(nums[i++],nums[j--]);
        i=k,j=nums.size()-1;
        while(i<j) swap(nums[i++],nums[j--]);
    }
};

/*
* o(n) 
* Using extra space approach we simply store the last k elemets in same order from n-k to n-1 in a temp vector and 
* we then pushback the reaining elements in the temp vector from index 0 to n-k-1; 
*/
class Solution {
public:
    void rotate(vector<int>& nums, int k) {
        k%=nums.size();
        vector<int> temp;
        int n = nums.size() - k;
        for(int i=n ; i<nums.size();i++)temp.push_back(nums[i]);
        for(int i=0;i<n;i++) temp.push_back(nums[i]);
        nums = temp;

    }
};

// o(n*k) we roatate elements of the vector one by one for k times and achieve k roatations.
// TLE
class Solution {
public:
    void rotate(vector<int>& nums, int k){
        int i, j, tmp,n = nums.size();
        k%=n;
        if (n==k) return;
        for(i=n-1; i>=n-k; i--) {
            tmp = nums[n-1];
            for (j=n-1;j>0;j--)nums[j] = nums[j-1];
            nums[0] = tmp;
        }   
    }
};
```

