void rotate(int nums[], int n, int k) {
    if (n == 0)
        return;

    k %= n;
    if (k == 0)
        return;

    // Method 1, 
    // reverse(nums, 0, n-k-1);
    // reverse(nums, n-k, n-1);
    // reverse(nums, 0, n-1);

    // Method 2. Swap int to its position directly, 
    // and store the next one that need be swapped. 
    // One tricky thing is to to avoid swapping the same int.
    int idx = 0;
    int distance = 0;
    int cur = nums[idx];
    for (int i = 0; i < n; ++i) {
        int next = (idx+k)%n;
        int temp = nums[next];
        nums[next] = cur;
        idx = next;
        cur = temp;

        // If distance is back. Move it one step forward.
        distance = (distance+k)%n;
        if (distance == 0) {
            idx = (idx+1)%n;
            cur = nums[idx];
        }
    }
}

void reverse(int nums[], int begin, int end) {
    while (begin < end) {
        swap (nums[begin++], nums[end--]);
    }
}

