class Solution {
    public int[] rotate(int[] nums, int k) {
        k = k % nums.length; //every number divisible by nums.length meaning it will go back to initial position, will only need to rotate  by the modulo
        reverse(nums, 0, nums.length - 1);  //reverse the whole array
        reverse(nums, 0, k -1); //reverse back the first k numbers to original order
        reverse(nums, k, nums.length  - 1); //reverse the remaining numbers  to original  order
        return nums;
    }

    private void reverse(int[] nums, int start, int end){
        while (start < end ){
            int  temp  = nums[start];
            nums[start] = nums[end];
            nums[end] = temp;
            start++;
            end--;
        }
    }
}
