class Solution {
public:
    void rotate(vector<int>& nums, int k) {
        int n=nums.size();
        k=k%n;
        if(k==0) return;
        int temp = nums[n-k-1];
        int j=n-k,i=0;
        while(j<n)
        {
            nums[j-1]=nums[i];
            nums[i]=nums[j];
            i++;j++;
        }
        nums[n-1]=temp;
    }
};

