class Solution {
    public void rotate(int[] nums, int k) {

        k=k% nums.length;
        if(k==0){
            return;
        }
        int start= 0;
        int end=nums.length-1;
        //reverse the window of size K
        reverse(nums,nums.length-k,end);
        //reverse the entire array
        reverse(nums,start,end);
        //reverse the window of size (n-k)
        reverse(nums,start+k,end);

    }

    public static void reverse(int[] arr,int start,int end){

        while (start<end){
            int tmp=arr[start];
            arr[start++]=arr[end];
            arr[end--]=tmp;
        }

    }
}

