class Solution {
    public void rotate(int[] nums, int k) {
        List<Integer> num = new ArrayList<>();
        for(int i=0;i<nums.length;i++){
            num.add(nums[i]);
        }
        int l=nums.length-1;
        while(k>0){
            if(l<0)
            l=nums.length-1;
            num.add(0,nums[l--]);
            --k;
        }
        for(int i=0;i<nums.length;i++){
            nums[i]=num.get(i);
        }
    }
} 
