function reverseInPlace(nums: number[], startIdx: number, endIdx: number) {
    while (startIdx < endIdx) {
        const tmp = nums[startIdx];
        nums[startIdx] = nums[endIdx];
        nums[endIdx] = tmp;
        startIdx++;
        endIdx--;
    }
}
function rotate(nums: number[], k: number): void {
    // when rotate nums.length times, we'll end up at the same place, 
    // so only k % nums.length times are useful
    k = k % nums.length; 

    // let's say: 1 2 3 4 5 6 7, k = 2

    // step 1: 5 4 3 2 1 6 7
    reverseInPlace(nums, 0, nums.length - 1 - k);

    // step 2: 5 4 3 2 1 7 6
    reverseInPlace(nums, nums.length - k, nums.length - 1);

    // step 3: 6 7 1 2 3 4 5
    reverseInPlace(nums, 0, nums.length - 1);
};
