struct ListNode *removeNthFromEnd(struct ListNode *head, int n) {

    struct ListNode* front = head;
    struct ListNode* behind = head;

    while (front != NULL) {
        front = front->next;

        if (n-- < 0) behind = behind->next;
    }
    if (n == 0) head = head->next;
    else behind->next = behind->next->next;
    return head;
}

