		t1 = t1->next;

	if (t1 == NULL) // if need to remove teh first Node
		return head->next;

	ListNode *t2 = head;
	while (t1->next) // find the node before n-th from the end
	{
		t1 = t1->next;
		t2 = t2->next;
	}
	t2->next = t2->next->next;

	return head;
}
