class Solution {
public:
    ListNode* removeNthFromEnd(ListNode* head, int n) {
        //if only one element 
        if(!head->next){
            return nullptr;
        }
        //if two element
        if(!head->next->next){
            if(n==1){
                head->next=nullptr;
                return head;
            }
            else{
                return head->next;
            }
        }
        //more than two element
        int ctr=0, length=0;
        ListNode *temp=head;
        ListNode *ptr =head;

        //checking length
        while(ptr){
            ptr = ptr->next;
            length++;
        }

        //1st edge case if first element removed
        if(n==length){
            return head->next;
        }
        //removing length-n element
        while(temp){
            //if element found is n-1th from end
            if(ctr==length-n-1){

                if(temp->next->next!=nullptr){
                    temp->next=temp->next->next;
                }
                //2nd edge case if last element removed
                else{
                    temp->next = nullptr;
                }
            }
            temp = temp->next;
            ctr++;
        }
        return head;
    }
};
